import React, { useState } from 'react';


interface TodoListItemProps {
  todo: Todo;
  toggleComplete1: ToggleComplete;
  deleteTodo: RemoveTodo;
  editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = ({
  todo,
  toggleComplete1,
  deleteTodo,
  editTodo,
}) => {
  const [isEditOn, setIsEditOn] = useState<boolean>(false);
  const [inputText, setInputText] = useState<string>(todo.text);

  const onDeleteTodo = () => {
    deleteTodo(todo);
  };

  const onSaveEdit = () => {
    const editedTodo = { ...todo, text: inputText };
    editTodo(editedTodo);
    setIsEditOn(false);
  };
  
  

  const onEditTodo = () => {
    setIsEditOn(true);
  };

  return (
    <li className="">
        <label>
            <div className=''>
            <input
                type="checkbox"
                onChange={() => toggleComplete1(todo)}
                checked={todo.complete}
              />
            </div>
              {isEditOn ? (
                <div className=''>
                  <div className=''>
                    <input
                      className=""
                      type=""
                      value={inputText}
                      onChange={(e) => setInputText(e.target.value)}
                    />
                  </div>
                  <div className=''>
                    <button onClick={onSaveEdit}>Save</button>
                    <button onClick={() => setIsEditOn(false)}>Cancel</button>
                  </div>
                </div>
              ) : (
                <div className=''>
                    <div className=''>{todo.text}</div>
                    <div className=''>
                        <div className='' onClick={onDeleteTodo}>Delete</div>
                        <div className=''onClick={onEditTodo}>Edit</div>
                    </div>
                </div>
              )}
        </label>
    </li>
  );
};
