import React, { useState } from 'react';


interface TodoListItemProps {
  todo: Todo;
  toggleComplete1: ToggleComplete;
  deleteTodo: RemoveTodo;
  editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = ({
  todo,
  toggleComplete1,
  deleteTodo,
  editTodo,
}) => {
  const [isEditOn, setIsEditOn] = useState<boolean>(false);
  const [inputText, setInputText] = useState<string>(todo.text);

  const onDeleteTodo = () => {
    deleteTodo(todo);
  };

  const onSaveEdit = () => {
    const editedTodo = { ...todo, text: inputText };
    editTodo(editedTodo);
    setIsEditOn(false);
  };
  
  

  const onEditTodo = () => {
    setIsEditOn(true);
  };

  return (
    <ul className="bg-white shadow transition duration-300 ease-in-out overflow-hidden sm:rounded-lg max-w-sm mx-auto mb-8 rounded-2xl ">
    <li>
        <div className="px-4 py-5 sm:px-6">
            <div className="flex items-center justify-between">
                <h3 className="text-lg leading-6 font-medium text-gray-900">{todo.text}</h3>
                <p className="mt-1 max-w-2xl text-sm text-gray-500">Description for Item 1</p>
            </div>
            <div className="mt-4 flex  justify-between">
                <p className="text-sm font-medium text-gray-500">Status: <span className="text-green-600">Active</span></p>
                <a onClick={onEditTodo} className="font-medium text-indigo-600 hover:text-indigo-500">Edit</a>
                <a onClick={onDeleteTodo} className="font-medium text-indigo-600 hover:text-indigo-500">Delete</a>
            </div>
        </div>
    </li>
</ul>
  );
};
