import React, { useState } from 'react'


interface  TodoListItemProps{
    todo: Todo;
    toggleComplete1: ToggleComplete;
    deleteTodo: RemoveTodo;
    editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = (TodoListItemProps) => {
    const {todo,toggleComplete1,deleteTodo,editTodo} = TodoListItemProps;
    const [isEditOn, setIsEditOn] = useState<boolean>(false);
    const [inputText, setInputText] = useState<string>(todo.text);

    const onDeleleTodo = () => {
        deleteTodo(todo);
    }

    const handleDelete = () =>{
        setIsEditOn(true)
        return(
            <input className="edit-input" type="text" value={inputText} onChange={(e) => onTodoUpdate(e)}/>
        );
    }

    const onTodoUpdate = (e: any) => {
        let text = e.target.value;
        setInputText(text);
        editTodo(text);
      }

  return (
    <li className={todo.complete ? "todo-row completed" : "todo-row"}>
        <label>
            <input
                type="checkbox"
                onChange={() => toggleComplete1(todo)}
                checked={todo.complete}
            />
            {todo.text}
            <button onClick={onDeleleTodo}>Delete</button>
            <button onClick={handleDelete}>Edit</button>
            {/* {isEditOn ?  : todo.text} */}
        </label>
    </li>
  )
}
