import React, { useState } from 'react';

interface TodoListItemProps {
  todo: Todo;
  // toggleComplete1: ToggleComplete;
  deleteTodo: RemoveTodo;
  editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = ({
  todo,
  // toggleComplete1,
  deleteTodo,
  editTodo,
}) => {
  const [isEditOn, setIsEditOn] = useState<boolean>(false);
  const [inputText, setInputText] = useState<string>(todo.text);

  const onDeleteTodo = () => {
    deleteTodo(todo);
  };

  const onSaveEdit = () => {
    const editedTodo = { ...todo, text: inputText };
    editTodo(editedTodo);
    setIsEditOn(false);
  };
  
  

  const onEditTodo = () => {
    setIsEditOn(true);
  };

  return (
    <li className={todo.complete ? "todo-row completed" : "todo-row"}>
      <label>
        {/* <input
          type="checkbox"
          onChange={() => toggleComplete1(todo)}
          checked={todo.complete}
        /> */}
        {isEditOn ? (
          <div>
            <input
              className="edit-input"
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
            />
            <button onClick={onSaveEdit}>Save</button>
            <button onClick={() => setIsEditOn(false)}>Cancel</button>
          </div>
        ) : (
          <div>
            {todo.text}
            <button onClick={onDeleteTodo}>Delete</button>
            <button onClick={onEditTodo}>Edit</button>
          </div>
        )}
      </label>
    </li>
  );
};
