import React, { useState } from 'react';


interface TodoListItemProps {
  todo: Todo;
  toggleComplete1: ToggleComplete;
  deleteTodo: RemoveTodo;
  editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = ({
  todo,
  toggleComplete1,
  deleteTodo,
  editTodo,
}) => {
  const [isEditOn, setIsEditOn] = useState<boolean>(false);
  const [inputText, setInputText] = useState<string>(todo.text);

  const onDeleteTodo = () => {
    deleteTodo(todo);
  };

  const onSaveEdit = () => {
    const editedTodo = { ...todo, text: inputText };
    editTodo(editedTodo);
    setIsEditOn(false);
  };
  
  

  const onEditTodo = () => {
    setIsEditOn(true);
  };

  return (
    <li>
      <label className='flex flex-basis'>
        <div className='basis-2/10'>
          <input
            type="checkbox"
            onChange={() => toggleComplete1(todo)}
            checked={todo.complete}
          />
        </div>
        {isEditOn ? (
          <div className='basis-8/10 flex flex-basis'>
            <div className=''>
              <input
                className=""
                type=""
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
              />
            </div>
            <div className=''>
              <button onClick={onSaveEdit}>Save</button>
              <button onClick={() => setIsEditOn(false)}>Cancel</button>
            </div>
          </div>
        ) : (
          <div className='basis-8/10 flex flex-basis'>
            <div className='basis-7/10'>{todo.text}</div>
            <div className='basis-3/10 flex justify-between'>
              <div className='' onClick={onDeleteTodo}>
                Delete
              </div>
              <div className='' onClick={onEditTodo}>
                Edit
              </div>
            </div>
          </div>
        )}
      </label>
    </li>
  );
  
};
