import React, { useState } from 'react';

interface TodoListItemProps {
  todo: Todo;
  toggleComplete1: ToggleComplete;
  deleteTodo: RemoveTodo;
  editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = ({
  todo,
  toggleComplete1,
  deleteTodo,
  editTodo,
}) => {
  const [isEditOn, setIsEditOn] = useState<boolean>(false);
  const [inputText, setInputText] = useState<string>(todo.text);

  const onDeleteTodo = () => {
    deleteTodo(todo);
  };

  const handleEdit = () => {
    setIsEditOn(true);
  };

  const onSaveEdit = () => {
    editTodo({ ...todo, text: inputText });
    setIsEditOn(false);
  };

  const onTodoUpdate = (e: any) => {
    let text = e.target.value;
    setInputText(text);
    editTodo(text);
  }

  return (
    <li className={todo.complete ? 'todo-row completed' : 'todo-row'}>
      <label>
        <input
          type="checkbox"
          onChange={() => toggleComplete1(todo)}
          checked={todo.complete}
        />
        {isEditOn ? (
          <input className="edit-input" type="text" value={inputText} onChange={(e) => onTodoUpdate(e)}/>
        ) : (
          <span>{todo.text}</span>
        )}
        {isEditOn ? (
          <div>
            <button onClick={onSaveEdit}>Save</button>
            <button onClick={() => setIsEditOn(false)}>Cancel</button>
          </div>
        ) : (
          <div>
            <button onClick={handleEdit}>Edit</button>
            <button onClick={onDeleteTodo}>Delete</button>
          </div>
        )}
      </label>
    </li>
  );
};
