import React, { useState } from 'react'


interface  TodoListItemProps{
    todo: Todo;
    toggleComplete1: ToggleComplete;
    deleteTodo: RemoveTodo;
    // editTodo: EditTodo;
}

export const TodoListItem: React.FC<TodoListItemProps> = (TodoListItemProps) => {
    const {todo,toggleComplete1,deleteTodo} = TodoListItemProps;

    const onDeleleTodo =() => {
        deleteTodo(todo);
    }

    const [isEditOn, setEditOn] = useState<boolean>(false)
    const [inputText,setInputTexr] = useState<string>(todo.text)

    const onEditTodo = () => {
        setEditOn(true);
        return(
            <input type='text'></input>
        )
    }

  return (
    <li className={todo.complete ? "todo-row completed" : "todo-row"}>
        <label>
            <input
                type="checkbox"
                onChange={() => toggleComplete1(todo)}
                checked={todo.complete}
            />
            {todo.text}
            <button onClick={onDeleleTodo}>Delete</button>
            <button onClick={onEditTodo}>Edit</button>
        </label>
    </li>
  )
}
