  //     if(todo ===selectedTodo)
  //       return {...todo, complete: !todo.complete}

  //   })) 
  // }

  const addTodo: AddTodo = newTodo => {
    if(newTodo !== "")
      setTodos([...todos,{text: newTodo, complete: false}])
  }

  return (
    <div className="App">
      <header className="App-header"> 
      TODOLIST
      </header>
      <Todoform addTodo = {addTodo}/>
    </div>
  );
}

export default App;
